import React,{useState , useEffect } from 'react';
import '../App.css';
import axios from "axios";
//import useForm from './LoginFormHandler';

const LoginForm = () => {
    let initform ={
      email:"",
      password:""
    }

    const [form,setForm] = useState(initform);
    useEffect(()=>{
      axios.post('http://localhost:3000/app/login/')
    })
    const handleChange = (e) => {
        e.preventDefault();
        const { value, name } = e.target;
        setForm({ ...form, [name]: value });
    }

    return (
      <div className="main-container col-md-12">
        <div className="container col-md-8">
        <form className="align-items-sm-center  ">
          <div className="col-md-3">
            <label htmlFor="inputEmail4" className="form-label">Email</label>
            <input type="email" value={form.email} onChange={handleChange} className="form-control" id="inputEmail4" />
          </div>
          <div className="col-md-3">
            <label htmlFor="inputPassword4" className="form-label">Password</label>
            <input type="password" value={form.password} onChange={handleChange}  className="form-control" id="inputPassword4" />
           </div>
           <br />
          <div className="col-12">
            <button type="submit"  className="btn btn-primary">Sign in</button>
          </div>
        </form>
      </div>
      </div>
    )
}
export default LoginForm;
